// Coloque aqui suas actions
export const login = (value) => ({ type: 'LOGIN', value });

export const saveCurrencies = (currencies) => ({
  type: 'SAVE_CURRENCIES', payload: currencies });

// requisito 4
// Os valores da chave currencies no estado global devem ser puxados através de uma requisição à API
export const sCoin = () => async (dispatch) => {
  const response = await fetch('https://economia.awesomeapi.com.br/json/all');
  const json = await response.json();
  const filterCoin = Object.keys(json).filter((coin) => coin !== 'USDT');
  dispatch(saveCurrencies(filterCoin));
};

// req 5, salvar no estado global
export const saveExpenses = (expenses) => ({
  type: 'SAVE_EXPENSES', payload: expenses });

export const mCoin = (expenses) => async (dispatch) => {
  const response = await fetch('https://economia.awesomeapi.com.br/json/all');
  const json = await response.json();
  dispatch(saveExpenses({ ...expenses, exchangeRates: json }));
};
