// Esse reducer será responsável por tratar as informações da pessoa usuária
const INITIAL_USER = {
  email: 'alguem@email.com',
};

function user(state = INITIAL_USER, action) {
  switch (action.type) {
  case 'LOGIN':
    return {
      ...state,
      email: action.value,
    };
  default:
    return { ...state };
  }
}

export default user;
